import { Component, OnInit } from '@angular/core';
import AuthorModel from 'src/app/models/author-model';
import BookModel from 'src/app/models/book-model';
import { DataService } from 'src/app/services/data.service';
import { NotifyService } from 'src/app/services/notify.service';

@Component({
    selector: 'app-insert',
    templateUrl: './insert.component.html',
    styleUrls: ['./insert.component.css']
})
export class InsertComponent implements OnInit {

    public authors: AuthorModel[];
    public book = new BookModel();
    private router: R

    public constructor(private dataService: DataService,
    private notifyService: NotifyService) { }

    public async ngOnInit() {

        try {
            this.authors = await this.dataService.getAllAuthors();

        }
        catch(err: any){
            this.notifyService.error(err);
        }
    }

    public async send() {

        try {
            await this.dataService.addBook(this.book);
            this.notifyService.success("Book added");

        }
        catch(err: any){
            this.notifyService.error(err);
        }

    }

}
<div>

     <form (ngSubmit)="send()">

        <label>Author: </label>
        <select [(ngModel)]="book.authorId">
            <option disabled selected value="">Select Author...</option>
            <option *ngFor="let a of authors"[value]="a.authorId">{{a.firstName}} {{a.lastName}}</option>
        </select>

        <label>Book Name: </label>
        <input type ="text" [(ngModel)]="book.bookName" >

        <label>Pages: </label>
        <input type ="number" [(ngModel)]="book.pages" >

        <label>Price: </label>
        <input type ="number" step="0.01" [(ngModel)]="book.price">

        <button>Add</button>

    </form>


</div>
