import { Component, EventEmitter, Input, Output } from '@angular/core';
import BookModel from 'src/app/models/book-model';

@Component({
  selector: 'app-book-card',
  templateUrl: './book-card.component.html',
  styleUrls: ['./book-card.component.css']
})
export class BookCardComponent {

@Input()
public book: BookModel;

@Output()
public deleteMe = new EventEmitter<number>(); // 

public async deleteBook() {
    this.deleteMe.emit(this.book.bookId)

}

}
<div>

  <span>Book Name: {{book.bookName}}</span>  
  <br>

  <span>Author: {{book.authorFullName}}</span>  
  <br>

  <span>Pages: {{book.pages}}</span>  
  <br>

  <span>Price: {{book.price | currency}}</span>  
  <br>

  <button (click)="deleteBook()">❌</button>

</div>
